void Transmission::Initialize( double forceLowGear )
{
	if (forceLowGear < 0.5)
		m_lowGearOnly = false;
	else
		m_lowGearOnly = true;
}
//
//	Enable the high gear solenoid
//
void Transmission::ShiftHigh( void )
{
	if (m_lowGearOnly)
		shifterSolenoid->Set( shifterSolenoid->kReverse );
	else
		shifterSolenoid->Set( shifterSolenoid->kForward );
}
//
//	Disable the high gear solenoid
//
void Transmission::ShiftLow( void )
{
	shifterSolenoid->Set( shifterSolenoid->kReverse );
}
