void Climber::SetMotorSpeed(int speed)
{
	switch (speed)
	{
	default:
	case CLIMBER_STOP:
		motor18->Set(0.0);
		motor19->Set(0.0);
		break;
	case CLIMBER_FORWARD:
		motor18->Set(climberSpeed);
		motor19->Set(climberSpeed);
		break;
	case CLIMBER_REVERSE:
		motor18->Set(-climberSpeed);
		motor19->Set(-climberSpeed);
		break;
	}
}
