void Gripper::SetGripperMotorSpeed(int speed)
{
#ifdef ROBOTNOTSTANDALONE

	double output = 0.0; 		//Default: off

	switch (speed)
	{
	default:
	case GRIPPER_STOP:
		output = 0.0;
		break;
	case GRIPPER_FORWARD:
		output = m_gripperSpeed;
		break;
	case GRIPPER_REVERSE:
		output = -m_gripperSpeed;
		break;
	}

	motorL7->Set(ControlMode::PercentOutput, output);
	motorR8->Set(ControlMode::PercentOutput, output);

#endif
}

void Gripper::SetWristMotorSpeed(int speed)
{
#ifdef ROBOTNOTSTANDALONE

	double output = 0.0; 		//Default: off

	switch (speed)
	{
	default:
	case WRIST_STOP:
		output = 0.0;
		break;
	case WRIST_IN:
		output = m_wristSpeed;
		break;
	case WRIST_OUT:
		output = -m_wristSpeed;
		break;
	}

	motorW9->Set(ControlMode::PercentOutput, output);

#endif
}

void Gripper::ExtensionPIDInit(double angle)
{
#ifdef ROBOTNOTSTANDALONE
	std::printf("2135: Extension Started\n");

	double target;

	target = (angle * COUNTS_PER_ROTATION) / 360.0;

	// Set Encoder Position to zero
	motorW9->SetSelectedSensorPosition(0, pidIndex, 0);

	// Set the mode and target
	motorW9->Set(ControlMode::Position, target);

	m_pidStarted = false;
#endif
}

void Gripper::ExtensionPIDExecute(void)
{
	// No work needed because Talon does it internally
}

bool Gripper::ExtensionPIDIsAtSetpoint(void)
{
	bool pidFinished = false;

	int closedLoopError = 0;	// Initialized for ROBOTNOTSTANDALONE mode

	// Detect if closed loop error has been updates
#ifdef ROBOTNOTSTANDALONE
	closedLoopError = motorW9->GetClosedLoopError(0);
	std::printf("2135: Closed loop error %d\n", closedLoopError);
	std::printf("2135: Encoder count %d\n", motorW9->GetSelectedSensorPosition(0));

	double motorOutput = motorW9->GetMotorOutputPercent();
	if (abs(motorOutput) >= 0.4) {
		m_pidStarted = true;
		std::printf("PID Started\n");
	}

	if (m_pidStarted == true && closedLoopError <= 20)
		pidFinished = true;
		std::printf("2135: Finished\n");
#endif
	return pidFinished;
}

void Gripper::ExtensionPIDStop(void)
{
#ifdef ROBOTNOTSTANDALONE
	std::printf("2135: Extension STOP\n");

	// Set driving mode and target
	motorW9->Set(ControlMode::PercentOutput, 0.0);
#endif
}
