/**
 * This function is called when the disabled button is hit.
 * You can use it to reset subsystems before shutting down.
 */
void Robot::DisabledInit(){
	std::printf("2135: ***** Disabled Init *****\n");
	drivetrain->Initialize();
	elevator->Initialize();
	elbow->Initialize();
	wrist->Initialize();
	intake->Initialize();
	lifter->Initialize();
}

void Robot::DisabledPeriodic() {
	frc::Scheduler::GetInstance()->Run();

	// If RoboRIO User button is pressed, dump all CAN faults
	if (frc::RobotController::GetUserButton() && !m_faultsCleared) {
	 	m_faultsCleared = true;
	 	RobotFaultDump();
	}
}

void Robot::AutonomousInit() {
	std::printf("2135: ***** Auton Init ***** - FMS Read Game Data\n");
	FMSGameDataRead();
	drivetrain->Initialize();
	elevator->Initialize();
	elbow->Initialize();
	wrist->Initialize();
	intake->Initialize();
	lifter->Initialize();
	autonomousCommand = chooser.GetSelected();
	if (autonomousCommand != nullptr)
		autonomousCommand->Start();
}

void Robot::AutonomousPeriodic() {
	frc::Scheduler::GetInstance()->Run();
}

void Robot::TeleopInit() {
	std::printf("2135: ***** Teleop Init *****\n");
	// This makes sure that the autonomous stops running when
	// teleop starts running. If you want the autonomous to
	// continue until interrupted by another command, remove
	// these lines or comment it out.
	if (autonomousCommand != nullptr)
		autonomousCommand->Cancel();
	drivetrain->Initialize();
	elevator->Initialize();
	elbow->Initialize();
	wrist->Initialize();
	intake->Initialize();
	lifter->Initialize();
}

void Robot::TeleopPeriodic() {
	frc::Scheduler::GetInstance()->Run();
}

