//	Fault handling utilities

void Robot::RobotFaultDump(void) {
	//	Print out talon SRX faults and clear sticky ones
	std::printf("2135: %s --------------\n", "FAULT DUMPS");
	drivetrain->FaultDump();
	elevator->FaultDump();
	elbow->FaultDump();
	wrist->FaultDump();
	intake->FaultDump();
	lifter->FaultDump();
	pneumatics->FaultDump();
	power->FaultDump();
}

