bool Magazine::GetSlotLimit(int slotNum) {
	bool	pressed;
	
	switch ( slotNum) {
	default:
	case 1:
		pressed = ( slotLimit1->GetVoltage() > 2.5 ) ? true : false;
		break;
	case 2:
		pressed = ( slotLimit2->GetVoltage() > 2.5 ) ? true : false;
		break;
	case 3:
		pressed = ( slotLimit3->GetVoltage() > 2.5 ) ? true : false;
		break;
	case 4:
		pressed = ( slotLimit4->GetVoltage() > 2.5 ) ? true : false;
		break;
	}
	
	return pressed;
}
